package com.mi.hundsun.oxchains.base.core.po.fn;import com.fasterxml.jackson.annotation.JsonFormat;import com.mi.hundsun.oxchains.base.common.baseMapper.GenericPo;import lombok.Data;import lombok.EqualsAndHashCode;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import java.util.function.Consumer;import javax.persistence.Table;import javax.persistence.Transient;import  java.util.Map;import  java.util.Date;import  java.util.HashMap;import  com.alibaba.fastjson.annotation.JSONField;import  java.math.BigDecimal;/** * 充币管理实体信息<br> * * @author bin * @date   2018-04-15 04:34:29 */@Data@EqualsAndHashCode(callSuper = true)@ApiModel(description = "充币管理")@Table(name = "fn_recharge_coin")public class RechargeCoin extends GenericPo<Integer> {    public static final String TABLE_NAME = "fn_recharge_coin";		/**用户ID**/	@ApiModelProperty(value = "用户ID")	private Integer userId;	/**订单号**/	@ApiModelProperty(value = "订单号")	private String orderNo;	/**平台收币地址 该地址每个客户分配一个，与客户--对应**/	@ApiModelProperty(value = "平台收币地址 该地址每个客户分配一个，与客户--对应")	private String platCollectAddr;	/**客户充币地址，客户是从哪一个地址往平台里面打币的**/	@ApiModelProperty(value = "客户充币地址，客户是从哪一个地址往平台里面打币的")	private String userRechargeAddr;	/**充币数量**/	@ApiModelProperty(value = "充币数量")	private BigDecimal amount;	/**区块链流水号**/	@ApiModelProperty(value = "区块链流水号")	private String txId;	/**币种**/	@ApiModelProperty(value = "币种")	private String coinCurrency;	/**状态 0,待审核:pending;1,审核通过:pass;2,审核不通过:nopass**/	@ApiModelProperty(value = "状态 0,待审核:pending;1,审核通过:pass;2,审核不通过:nopass")	private Integer state;	@Transient	private String stateFormatter ;	/**审核员**/	@ApiModelProperty(value = "审核员")	private String checker;	/**审核时间**/	@ApiModelProperty(value = "审核时间")	@JSONField(format = "yyyy-MM-dd HH:mm:ss")	@JsonFormat(timezone = "GMT+8", pattern = "yyyy-MM-dd HH:mm:ss")	private Date checkTime;	/**审核备注**/	@ApiModelProperty(value = "审核备注")	private String checkRemark;	public String getStateFormatter() {	    if(null == stateFormatter || "".equals(stateFormatter)){		    return STATE.getValue(getState());		}	    return this.stateFormatter;	}	public void setStateFormatter(String stateFormatter) {	    this.stateFormatter=stateFormatter;	}	/**0,待审核:pending<br>1,审核通过:pass<br>2,审核不通过:nopass**/	public enum STATE {			/**0,待审核:pending**/		PENDING("待审核",0),			/**1,审核通过:pass**/		PASS("审核通过",1),			/**2,审核不通过:nopass**/		NOPASS("审核不通过",2);			public final int code;		public final String value;		private static Map<Integer, String> map = new HashMap<Integer, String>();			private STATE(String value, int code) {			this.code = code;			this.value = value;		}			public static String getValue(Integer code) {			if (null == code) {				return null;			}			for (STATE state : STATE.values()) {				if (state.code == code) { 					return state.value;				}			}			return null;		}			public static Integer getCode(String value) {			if (null == value  || "".equals(value)) {					return null;			}			for (STATE state : STATE.values()) {				if (state.value.equals(value)) { 					return state.code;				}			}			return null;		}			public static  Map<Integer, String> getEnumMap() {			if(map.size() == 0){				for (STATE state : STATE.values()) {					map.put(state.code,state.value);				}			}			return map;		}	}	    public RechargeCoin(){}    public RechargeCoin(Consumer<RechargeCoin> consumer){    consumer.accept(this);    }}